import React from 'react';
import {
  Flex,
  Box,
  Heading,
  FormControl,
  FormLabel,
  Input,
  Button,
  FormHelperText,
  SimpleGrid,
  VStack,
} from '@chakra-ui/react';

export default function LoginForm() {
    return (
        <SimpleGrid >
            <VStack borderRadius={10} borderWidth={2} p={10} >
                <VStack>
                <FormControl>
                    <FormLabel>Email address</FormLabel>
                    <Input type='email' />
                    <FormHelperText>We'll never share your email.</FormHelperText>
                </FormControl>
                </VStack>
            </VStack>
        </SimpleGrid>
