import React, { useCallback, useEffect, useState, useMemo } from 'react';
import {
  ChakraProvider,
  Box,
  Flex,
  Text,
  VStack,
  Grid,
  Button,
  useToast,
  Code,
  HStack,
  Heading,
  theme,
  Input,
  SimpleGrid,
  Img,
  Container,
} from '@chakra-ui/react';
import { Tabs, TabList, TabPanels, Tab, TabPanel } from '@chakra-ui/react';
import { FormControl, FormLabel } from '@chakra-ui/react';
import { ColorModeSwitcher } from './ColorModeSwitcher';

import * as web3 from '@solana/web3.js';
import {
  ConnectionProvider,
  WalletProvider,
  useConnection,
  useWallet,
} from '@solana/wallet-adapter-react';
import {
  getPhantomWallet,
  getSolflareWallet,
  getSolletWallet,
  getSolletExtensionWallet,
  getLedgerWallet,
} from '@solana/wallet-adapter-wallets';
import {
  WalletModalProvider,
  WalletMultiButton,
  WalletDisconnectButton,
} from '@solana/wallet-adapter-react-ui';
import { Greet } from './Greet';
import logo from './logo.svg';
import phoneUI from './components/phoneUI.svg';

require('@solana/wallet-adapter-react-ui/styles.css');

function WalletNotConnected() {
  return (
    <VStack height="70vh" justify="space-around">
      <VStack>
        <Text fontSize="2xl">
          {' '}
          Looks like your wallet is not connnected. Connect a wallet to get
          started!
        </Text>
        <WalletMultiButton />
      </VStack>
    </VStack>
  );
}

function useSolanaAccount() {
  const [account, setAccount] = useState(null);
  const [transactions, setTransactions] = useState(null);
  const { connection } = useConnection();
  const { publicKey } = useWallet();

  const init = useCallback(async () => {
    if (publicKey) {
      let acc = await connection.getAccountInfo(publicKey);
      setAccount(acc);
      let transactions = await connection.getConfirmedSignaturesForAddress2(
        publicKey,
        {
          limit: 10,
        }
      );
      setTransactions(transactions);
    }
  }, [publicKey, connection]);

  useEffect(() => {
    if (publicKey) {
      setInterval(init, 1000);
    }
  }, [init, publicKey]);

  return { account, transactions };
}

function Home() {
  const { connection } = useConnection();
  const { publicKey } = useWallet();
  const { account, transactions } = useSolanaAccount();
  const toast = useToast();
  const [airdropProcessing, setAirdropProcessing] = useState(false);

  const getAirdrop = useCallback(async () => {
    setAirdropProcessing(true);
    try {
      var airdropSignature = await connection.requestAirdrop(
        publicKey,
        web3.LAMPORTS_PER_SOL
      );
      await connection.confirmTransaction(airdropSignature);
    } catch (error) {
      console.log(error);
      toast({ title: 'Airdrop failed', description: 'unknown error' });
    }
    setAirdropProcessing(false);
  }, [toast, publicKey, connection]);

  return (
    <Box textAlign="left" fontSize="xl" background='linear-gradient(144deg, #242038 20%, #522DA9 80%, #432C87)'>
      <Grid minH="100vh" p={3}>
        <Tabs variant="soft-rounded" colorScheme="green">
          <TabList width="full">
            <HStack justify="space-between" width="full">
              <HStack>
                <Img src={logo} />
                <Tab>Home</Tab>
                <Tab>Transaction History</Tab>
              </HStack>
              <HStack>
                <WalletMultiButton />
                {publicKey && <WalletDisconnectButton bg="pink" />}
                <ColorModeSwitcher justifySelf="flex-end" />
              </HStack>
            </HStack>
          </TabList>
          <TabPanels>
            <TabPanel>
              {/* {publicKey && (
                <SimpleGrid columns={2} spacing={10}>
                  <VStack spacing={8} borderRadius={10} borderWidth={2} p={10}>
                    <FormControl id="pubkey">
                      <FormLabel>Wallet Public Key</FormLabel>
                      <Input
                        type="text"
                        value={publicKey.toBase58()}
                        readOnly
                      />
                      <div className='getting-started'>
                        <Box as="button" bg="green" w="120px" h="150px">This is a button</Box>
                     </div>
                    </FormControl>
                    <FormControl id="balance">
                      <FormLabel>Balance</FormLabel>
                      <Input
                        type="text"
                        value={
                          account
                            ? account.lamports / web3.LAMPORTS_PER_SOL + ' SOL'
                            : 'Loading..'
                        }
                        readOnly
                      />
                    </FormControl>
                    <Button onClick={getAirdrop} isLoading={airdropProcessing}>
                      Get Airdrop of 1 SOL
                    </Button>
                  </VStack>
                  <VStack>
                    <Greet />
                  </VStack>
                </SimpleGrid>
              )} */}
              <Box as="section" className='header' mt="170" >
                <VStack >
                <HStack>
                  <Img src={phoneUI} />
                </HStack>
                  <VStack justifyContent="space-evenly" align="left" >
                    <Heading as="h1" size="lg" fontSize='60px' fontWeight="600" mb="10" >A Passport NFT for your pet</Heading>
                    <Box maxWidth="750px" pb="10" align="left" >
                      <Text fontSize="24px" fontWeight="400" >Create a profile for your pet and we'll convert your pets information into a personalized NFT you can use to show proof-of-ownership and proof-of-records.</Text>
                    </Box>
                    <Button w="120px">Get Started</Button>
                  </VStack>
                </VStack>
                
                
              </Box>
            </TabPanel>
            <TabPanel>
              {publicKey && (
                <VStack spacing={8}>
                  <Heading>Transaction History</Heading>
                  {transactions && (
                    <VStack>
                      {transactions.map((v, i, arr) => (
                        <HStack key={'transaction-' + i}>
                          <Text>Signature: </Text>
                          <Code>{v.signature}</Code>
                        </HStack>
                      ))}
                    </VStack>
                  )}
                </VStack>
              )}
              {!publicKey && <WalletNotConnected />}
            </TabPanel>
          </TabPanels>
        </Tabs>
      </Grid>
    </Box>
  );
}

function App() {
  const network = 'devnet';
  const endpoint = web3.clusterApiUrl(network);
  const wallets = useMemo(
    () => [
      getPhantomWallet(),
      getSolflareWallet(),
      getSolletWallet({ network }),
      getSolletExtensionWallet({ network }),
      getLedgerWallet(),
    ],
    [network]
  );

  return (
    <>

    <ChakraProvider theme={theme}>
      <ConnectionProvider endpoint={endpoint}>
        <WalletProvider wallets={wallets} autoConnect>
          <WalletModalProvider>
            <Home></Home>
          </WalletModalProvider>
        </WalletProvider>
      </ConnectionProvider>
    </ChakraProvider>
    </>
  );
}

export default App;
