  FormHelperText,
  SimpleGrid,
  VStack,
} from '@chakra-ui/react';

export default function LoginForm() {
    return (
        <SimpleGrid spacing={10}>
            <VStack spacing={5} borderRadius={10} borderWidth={2} p={10}>
            <FormControl>
                <FormLabel>Email address</FormLabel>
